import { handleActions } from "redux-actions";
import { loginUser, loginSuccess } from "./action";

const DEFAULT_STATE = {
  status: "",
  user: {
    email: "",
    password: "",
  },
  token,
};

const handlers = {
  [loginUser]: (state) => ({ ...state, status: "PENDING" }),

  [loginSuccess]: (state, action) => {
    console.log("---> state action", state, "action", action);

    if (action.error) {
      return { ...state, status: "ERROR" };
    } else {
      const user = { ...action.payload };

      return { ...state, status: "SUCCESS", user };
    }
  },
};

export default handleActions(handlers, DEFAULT_STATE);
