import axios from "axios";

export const loginQuery = (request) => {
  const response = axios.post("https://reqres.in/api/login", request.payload);
  console.log("response",response);
  response.then((data)=>{
    console.log("data",data);

    return data;

  }).catch((err)=>{
    console.log("errrrrrr",err);

    return err;
  })
};
