/* eslint-disable*/

import React, { useState } from "react";
import { connect } from "react-redux";
import { getPosts, statusUnset } from "./redux/auth/action";

const Login = ({ dispatch, status, itemsPosts, actionCreated }) => {
  const [elementName, setElementName] = React.useState("");

  return (
    <div>
      <h3>Login</h3>
      <input
        type="text"
        value={elementName}
        placeholder="Element name added"
        onChange={(e) => setElementName(e.target.value)}
      />
      <button>SUBMIT</button>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    status: state,
    // itemsPosts: state.login.items,
    // actionCreated: state.login.newAction,
  };
};

export default connect(
  mapStateToProps,
  null // Generaly its the place of mapStateToDispatch
)(Login);
