import { handleActions } from "redux-actions";
import { loginUser, loginSuccess } from "./action";

const DEFAULT_STATE = {
  status: "",
  // user: {
  //   email: "",
  //   password: "",
  // },
  token: "",
};

const handlers = {
  [loginUser]: (state) => ({ ...state, status: "PENDING" }),

  [loginSuccess]: (state, action) => {
    if (action.error) {
      return { ...state, status: "ERROR" };
    } else {
      console.log("TOKEN", action.payload);
      let token;
      action.payload
        .then((data) => {
          token = data.token;
          // newState = { ...state, status: "SUCCESS", token };
        })
        .catch((err) => {
          console.log("errrrrrr", err);
          // newState = { ...state, status: "ERROR" };
        });

      return { ...state, status: "SUCCESS", token };
    }
  },
};

export default handleActions(handlers, DEFAULT_STATE);
